import Middleware from './middleware'
import { Auth, authMiddleware, ExpiredAuthSessionError } from '~auth/runtime'

// Active schemes
import { default as Scheme$a18a } from '/Users/leandro/Workspace/ibict/pinakes_frontend/plugins/keycloak.js'

Middleware.auth = authMiddleware

export default function (ctx, inject) {
  // Options
  const options = {
  "resetOnError": false,
  "ignoreExceptions": false,
  "scopeKey": "scope",
  "rewriteRedirects": true,
  "fullPathRedirect": false,
  "watchLoggedIn": true,
  "redirect": {
    "login": "/",
    "logout": "/",
    "home": "/home",
    "callback": "/auth/callback"
  },
  "vuex": {
    "namespace": "auth"
  },
  "cookie": {
    "prefix": "auth.",
    "options": {
      "path": "/"
    }
  },
  "localStorage": {
    "prefix": "auth."
  },
  "defaultStrategy": "keycloak"
}

  // Create a new Auth instance
  const $auth = new Auth(ctx, options)

  // Register strategies
  // keycloak
  $auth.registerStrategy('keycloak', new Scheme$a18a($auth, {
  "endpoints": {
    "authorization": "https://cobib-keycloak.tcti.ibict.br/realms/ibict/protocol/openid-connect/auth",
    "userInfo": "https://cobib-keycloak.tcti.ibict.br/realms/ibict/protocol/openid-connect/userinfo",
    "token": "https://cobib-keycloak.tcti.ibict.br/realms/ibict/protocol/openid-connect/token",
    "logout": "https://cobib-keycloak.tcti.ibict.br/realms/ibict/protocol/openid-connect/logout"
  },
  "token": {
    "property": "access_token",
    "type": "Bearer",
    "name": "Authorization",
    "maxAge": 1800
  },
  "refreshToken": {
    "property": "refresh_token",
    "maxAge": 2592000
  },
  "logoutRedirectUri": "https://frontend-ccn.tcti.ibict.br/",
  "responseType": "code",
  "grantType": "authorization_code",
  "clientId": "ibict-frontend",
  "scope": [
    "profile",
    "email",
    "realm_access.roles",
    "openid"
  ],
  "codeChallengeMethod": "S256",
  "name": "keycloak"
}))

  // Inject it to nuxt context as $auth
  inject('auth', $auth)
  ctx.$auth = $auth

  // Initialize auth
  return $auth.init().catch(error => {
    if (process.client) {
      // Don't console log expired auth session errors. This error is common, and expected to happen.
      // The error happens whenever the user does an ssr request (reload/initial navigation) with an expired refresh
      // token. We don't want to log this as an error.
      if (error instanceof ExpiredAuthSessionError) {
        return
      }

      console.error('[ERROR] [AUTH]', error)
    }
  })
}
